mapper.select:
SELECT * FROM smbms_user WHERE userCode != 'admin'
AND ( userCode LIKE CONCAT('%', #{keyword}, '%') OR userName LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%') OR address LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY ${sortField} ${sortOrder}
ORDER BY id DESC
LIMIT #{offset}, #{pageSize}

mapper.insert:
INSERT INTO smbms_user (
    userCode, userName, userPassword, salt, status, gender, 
    birthday, phone, address, headUrl, userRole, 
    createdBy, creationDate, modifyBy, modifyDate
) VALUES (
    #{userCode}, #{userName}, #{userPassword}, #{salt}, #{status}, #{gender},
    #{birthday}, #{phone}, #{address}, #{headUrl}, #{userRole},
    #{createdBy}, #{creationDate}, #{modifyBy}, #{modifyDate}
)
