mapper.select:
SELECT b.*, p.proName as providerName FROM smbms_bill b LEFT JOIN smbms_provider p ON b.providerId = p.id
AND ( b.billCode LIKE CONCAT('%', #{keyword}, '%') OR b.productName LIKE CONCAT('%', #{keyword}, '%') OR b.productDesc LIKE CONCAT('%', #{keyword}, '%') OR p.proName LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY ${sortField} ${sortOrder}
ORDER BY b.creationDate DESC
LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM smbms_bill
WHERE DATE(creationDate) = #{date}

SELECT COUNT(*)
FROM smbms_bill
WHERE DATE(creationDate) BETWEEN #{startDate} AND #{endDate}

SELECT COALESCE(SUM(totalPrice), 0)
FROM smbms_bill
WHERE DATE(creationDate) BETWEEN #{startDate} AND #{endDate}

mapper.insert:
INSERT INTO smbms_bill (
    billCode, productName, productDesc, productUnit, productCount,
    totalPrice, isPayment, providerId, createdBy, creationDate,
    modifyBy, modifyDate
) VALUES (
    #{billCode}, #{productName}, #{productDesc}, #{productUnit}, #{productCount},
    #{totalPrice}, #{isPayment}, #{providerId}, #{createdBy}, #{creationDate},
    #{modifyBy}, #{modifyDate}
)
