mapper.select:
SELECT * FROM smbms_provider
AND ( proCode LIKE CONCAT('%', #{keyword}, '%') OR proName LIKE CONCAT('%', #{keyword}, '%') OR proContact LIKE CONCAT('%', #{keyword}, '%') OR proPhone LIKE CONCAT('%', #{keyword}, '%') OR proAddress LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY ${sortField} ${sortOrder}
ORDER BY id DESC
LIMIT #{offset}, #{pageSize}

mapper.insert:
INSERT INTO smbms_provider (
    proCode, proName, proDesc, proContact, proPhone,
    proAddress, proFax, createdBy, creationDate, modifyBy, modifyDate
) VALUES (
    #{proCode}, #{proName}, #{proDesc}, #{proContact}, #{proPhone},
    #{proAddress}, #{proFax}, #{createdBy}, #{creationDate}, #{modifyBy}, #{modifyDate}
)
